# Write your MySQL query statement below
select * from ((select A.id as id, A.visit_date as visit_date, A.people as people from (Stadium A inner join Stadium B on A.id = B.id - 1) inner join Stadium C on B.id = C.id - 1 where A.people >= 100 and B.people >= 100 and C.people >= 100 order by A.visit_date) union (select B.id as id, B.visit_date as visit_date, B.people as people from (Stadium A inner join Stadium B on A.id = B.id - 1) inner join Stadium C on B.id = C.id - 1 where A.people >= 100 and B.people >= 100 and C.people >= 100 order by B.visit_date) union (select C.id as id, C.visit_date as visit_date, C.people as people from (Stadium A inner join Stadium B on A.id = B.id - 1) inner join Stadium C on B.id = C.id - 1 where A.people >= 100 and B.people >= 100 and C.people >= 100 order by C.visit_date)) as D order by D.visit_date;
