# Write your MySQL query statement below
with cte1 as (select visited_on, (sum(sum(amount)) over(order by visited_on range between interval 6 day preceding and current row)) as amount, (rank() over(order by visited_on)) as rnk from Customer group by visited_on order by visited_on), cte2 as (select min(visited_on) + interval 6 day as start_date from Customer) select cte1.visited_on, cte1.amount, round(cte1.amount / 7.0, 2) as average_amount from cte1 inner join cte2 on cte1.visited_on >= cte2.start_date;
