# Write your MySQL query statement below
select A.item_category as Category, sum(if(weekday(B.order_date) = 0, B.quantity, 0)) as 'Monday', sum(if(weekday(B.order_date) = 1, B.quantity, 0)) as 'Tuesday', sum(if(weekday(B.order_date) = 2, B.quantity, 0)) as 'Wednesday', sum(if(weekday(B.order_date) = 3, B.quantity, 0)) as 'Thursday', sum(if(weekday(B.order_date) = 4, B.quantity, 0)) as 'Friday', sum(if(weekday(B.order_date) = 5, B.quantity, 0)) as 'Saturday', sum(if(weekday(B.order_date) = 6, B.quantity, 0)) as 'Sunday' from Items A left join Orders B on A.item_id = B.item_id group by Category order by Category;
