# Write your MySQL query statement below
select A.name, A.mail from Users A inner join ((select gold_medal as user_id from Contests group by gold_medal having count(*) >= 3) union (select user_id from (select user_id, (contest_id - row_number() over(partition by user_id order by contest_id asc)) as ofs from ((select gold_medal as user_id, contest_id from Contests) union all (select silver_medal as user_id, contest_id from Contests) union all (select bronze_medal as user_id, contest_id from Contests)) C) D group by user_id, ofs having count(*) >= 3)) B on A.user_id = B.user_id;
