inline int mod_fact(int x) {
	int id = x / DELTA, y = id * DELTA, ret = fct[id];
	for( ; (++y) <= x; ret = (LL)ret * y % mod);
	return ret;
}
inline int mod_inv(int x) {
	return x <= 1 ? x : mod - (int)(mod / x * (LL)mod_inv(mod % x) % mod);
}
inline int mod_binom(int n, int m) {
	return n < m ? 0 : (LL)mod_fact(n) * mod_inv((LL)mod_fact(m) * mod_fact(n - m) % mod) % mod;
}
